import React, { useEffect, useState } from 'react';
import Listing from '../components/Listing'; // Assuming you have defined the ListingCard component
import { host } from '../config/config';
import axios from 'axios';

const Listings = () => {
  const [listings, setListings] = useState([]);

  useEffect(() => {
    // Fetch data from the API and set it in the listings state
    // Replace 'API_URL' with the actual API endpoint
    axios.get(`${host}/alllistings`)
      .then((response) => response.json())
      .then((data) => setListings(data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div>
      {/* Use the map function to loop through listings and render ListingCard */}
      {listings.map((listing) => (
        <Listing key={listing.id} listing={listing} />
      ))}
    </div>
  );
};

export default Listings;
